INSERT INTO `mob_chat_db` (`Line_ID`, `Color_Code`, `Dialog`) VALUES
(1, 16711680, 'Fraco! Me desafie se tiver alguma coragem!'),
(2, 16711680, 'Impressionante! Eu me pergunto até onde sua imprudencia irá levá-lo.'),
(3, 16711680, 'Tenho pena de você estar contra mim. Agora se prepare para meu ataque!'),
(4, 16711680, 'Meus servos fiéis! O recebram com uma morte dolorosa!'),
(5, 16711680, 'Não fuja!'),
(6, 16711680, 'Vocês'),
(7, 16711680, 'Subalternos inúteis!... Tudo bem'),
(8, 16711680, 'Reze para seus deuses!'),
(9, 16711680, 'Você ainda acha que é um jogo para mim?'),
(10, 16711680, 'Desapareça!'),
(11, 16711680, 'Vamos ver por quanto tempo você pode agüentar meu poder!'),
(12, 16711680, 'Isso é tudo que você tem?'),
(13, 16711680, 'Você está me fazendo cócegas!'),
(14, 16711680, 'Chama isso de ataque? Assista e aprenda'),
(15, 16711680, 'É hora de terminar o jogo!'),
(16, 16711680, 'Ha! Você é mais forte do que eu pensava!'),
(17, 16711680, 'Não! Isso não poder estar acontecendo! Eu sou Satan Morroc'),
(18, 16711680, 'Eu nunca morrei! Sempre estarei voltando para lhe pegar!'),
(19, 16711680, 'Eu nasci para conquistar este mundo! Nada poderá me impedir!'),
(20, 16711680, 'Seus dias estão contados!'),
(21, 16711680, 'Strike! Meus dedos dilaceradores de aço!'),
(22, 16711680, 'Hahaha! Me diga quem sou! Eo sou Bafomé! O herdeiro do inferno!'),
(23, 16711680, 'Aproveite sua vida enquanto pode! Sua esperança em breve irá se transformar em desespero!'),
(24, 16711680, 'Quando você aprenderá a lição? Quando morrer?'),
(25, 16711680, 'Não... Eu não posso perder! Não vou implorar por minha vida! Eu não estou fugindo! Eu não aceito isso como derrota!'),
(26, 16711680, 'Argh... Eu... Eu'),
(27, 16711680, 'O que você quer de mim?'),
(28, 16711680, 'Não! Eu não fiz isso! Ele é quem planejou tudo isso!'),
(29, 16711680, 'Eu só queria encontrar a paz..! É por isso que eu estava fugindo!'),
(30, 16711680, 'Ahhhh!!! Agora'),
(31, 16711680, 'Moscas irritantes!! Saiam de mim!'),
(32, 16711680, 'Sofra no Inferno!'),
(33, 16711680, 'Muahahaha! Sinta a fúria da terra!!!'),
(34, 16711680, 'Não... Eu não irei aceitar esta derrota!'),
(35, 16711680, 'Será que me bater irá aliviar a sua solidão? Por que você não fica aqui comigo para sempre'),
(36, 16711680, 'Você esqueceu o significado do tempo. Eu me pergunto quanto tempo você poderá durar aqui...'),
(37, 16711680, 'Existe alguém esperando por você? Desista de tudo'),
(38, 16711680, 'Esqueça sua vida e fique confinado aqui. Você vai desejar a liberdade preso em cativeiro cativeiro !!'),
(39, 16711680, 'Quais serão as mudanças no mundo exterior'),
(40, 16711680, 'Sim! Desejar a sua liberdade neste local confinado');
